let slides=document.querySelectorAll(".slider__img"),leftArrow=document.querySelector(".arrow__left"),rightArrow=document.querySelector(".arrow__right");function sliderFunction(e){for(let t=0;t<slides.length;t++){let r=slides[t],i=r.getAttribute("slider-position");console.log(t+" - "+i),e?0==i?i=slides.length-1:i--:i==slides.length-1?i=0:i++,r.style.opacity=0==i?"1":"0",r.setAttribute("slider-position",i)}}leftArrow.addEventListener("click",()=>{sliderFunction(!0)}),rightArrow.addEventListener("click",()=>{sliderFunction(!1)}),sliderFunction();